# Use FULL_DATETIME_FORMAT for both DATE_FORMAT and DATETIME_FORMAT,
# to avoid redundancy and ensure consistent date and time formatting.
FULL_DATETIME_FORMAT = "j F Y H:i"
DATE_FORMAT = FULL_DATETIME_FORMAT
DATETIME_FORMAT = FULL_DATETIME_FORMAT
YEAR_MONTH_FORMAT = "F Y"
MONTH_DAY_FORMAT = "j F"
SHORT_DATE_FORMAT = "j F Y"
SHORT_DATETIME_FORMAT = FULL_DATETIME_FORMAT
FIRST_DAY_OF_WEEK = 0  # Sunday

# The *_INPUT_FORMATS strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior
DATE_INPUT_FORMATS = [
    "%Y/%m/%d",  # '2006/10/25'
]
TIME_INPUT_FORMATS = [
    "%H:%M",  # '14:30'
    "%H:%M:%S",  # '14:30:59'
]
DATETIME_INPUT_FORMATS = [
    "%Y/%m/%d %H:%M",  # '2006/10/25 14:30'
    "%Y/%m/%d %H:%M:%S",  # '2006/10/25 14:30:59'
]

# Use the same separators as in the Django documentation
DECIMAL_SEPARATOR = "."
THOUSAND_SEPARATOR = ","
NUMBER_GROUPING = 3
