# The date and time format strings use the Django date format syntax
# for consistency with the rest of the Django framework.
DATE_FORMAT = "d F Y"
TIME_FORMAT = "H:i"
DATETIME_FORMAT = "d F Y H:i"
YEAR_MONTH_FORMAT = "F Y"
MONTH_DAY_FORMAT = "j F"
SHORT_DATE_FORMAT = "d.m.Y"
SHORT_DATETIME_FORMAT = "d.m.Y H:i"

# The input format strings use the Python strftime format syntax
# for consistency with the Python standard library.
DATE_INPUT_FORMATS = (
    "%d-%m-%Y", "%d/%m/%Y", "%Y-%m-%d", "%m/%d/%Y", "%m/%d/%y"
)
TIME_INPUT_FORMATS = (
    "%H:%M:%S", "%H:%M", "%I:%M:%S %p", "%I:%M %p"
)
DATETIME_INPUT_FORMATS = (
    "%d-%m-%Y %H:%M:%S", "%d/%m/%Y %H:%M:%S",
    "%Y-%m-%d %H:%M:%S", "%Y-%m-%dT%H:%M:%S",
    "%d/%m/%Y %H:%M", "%d-%m-%Y %H:%M",
    "%Y-%m-%d %H:%M"
)

# Use a period as the decimal separator and a non-breaking space as the
# thousand separator, for internationalization.
DECIMAL_SEPARATOR = "."
THOUSAND_SEPARATOR = "\u00A0"  # Non-breaking space
NUMBER_GROUPING = 3
