# The DATE_FORMAT string uses the Django date format syntax,
# see https://docs.djangoproject.com/en/dev/ref/templates/builtins/#date
DATE_FORMAT = "j F Y" # Example: 15 February 2023
TIME_FORMAT = "G:i"   # Example: 15:30
DATETIME_FORMAT = "j F Y G:i" # Example: 15 February 2023 15:30
YEAR_MONTH_FORMAT = "F Y" # Example: February 2023
MONTH_DAY_FORMAT = "F j" # Example: February 15
SHORT_DATE_FORMAT = "d/m/Y" # Example: 15/02/2023
SHORT_DATETIME_FORMAT = "d/m/Y G:i" # Example: 15/02/2023 15:30
FIRST_DAY_OF_WEEK = 1 # Example: Monday

# The *_INPUT_FORMATS strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior
DATE_INPUT_FORMATS = ("j F Y", "d/m/Y")
TIME_INPUT_FORMATS = ("G:i", "H:i:s")
DATETIME_INPUT_FORMATS = ("j F Y G:i", "d/m/Y H:i:s")

# The DECIMAL_SEPARATOR and THOUSAND_SEPARATOR are used for formatting numbers
DECIMAL_SEPARATOR = "."
THOUSAND_SEPARATOR = ","
NUMBER_GROUPING = 3
