# The DATE_FORMAT string uses the Django date format syntax,
# see https://docs.djangoproject.com/en/dev/ref/templates/builtins/#date
DATE_FORMAT = "F d, Y" # Example: February 23, 2022
TIME_FORMAT = "g:i A" # Example: 3:45 PM
DATETIME_FORMAT = "F d, Y g:i A" # Example: February 23, 2022 3:45 PM
YEAR_MONTH_FORMAT = "F Y" # Example: February 2022
MONTH_DAY_FORMAT = "F d" # Example: February 23
SHORT_DATE_FORMAT = "m/d/Y" # Example: 02/23/2022
SHORT_DATETIME_FORMAT = "m/d/Y g:i A" # Example: 02/23/2022 3:45 PM
FIRST_DAY_OF_WEEK = 1 # Example: Monday is the first day of the week

# The *_INPUT_FORMATS strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior
DATE_INPUT_FORMATS = (
    "%m/%d/%Y", # Example: 02/23/2022
    "%m/%d/%y", # Example: 02/23/22
    "%b %d %Y", # Example: Feb 23 2022
    "%b %d, %Y", # Example: Feb 23, 2022
)
TIME_INPUT_FORMATS = (
    "%I:%M %p", # Example: 03:45 PM
    "%I:%M:%S %p", # Example: 03:45:00 PM
)
DATETIME_INPUT_FORMATS = (
    "%m/%d/%Y %I:%M %p", # Example: 02/23/2022 03:45 PM
    "%m/%d/%Y %I:%M:%S %p", # Example: 02/23/2022 03:45:00 PM
    "%m/%d/%y %I:%M %p", # Example: 02/23/22 03:45 PM
    "%m/%d/%y %I:%M:%S %p", # Example: 02/23/22 03:45:00 PM
    "%b %d %Y %I:%M %p", # Example: Feb 23 2022 03:45 PM
    "%b %d, %Y %I:%M %p", # Example: Feb 23, 2022 03:45 PM
)

DECIMAL_SEPARATOR = "."
THOUSAND_SEPARATOR = ","
NUMBER_GROUPING = 3 # Grouping by thousands
