#
# The *_FORMAT strings use the Django date format syntax,
# see https://docs.djangoproject.com/en/dev/ref/templates/builtins/#date
#
# The *_INPUT_FORMATS strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior

DATE_FORMAT = "j N Y"
DATETIME_FORMAT = "j N Y, G.i"
TIME_FORMAT = "G.i"
YEAR_MONTH_FORMAT = "F Y"
MONTH_DAY_FORMAT = "j F"
SHORT_DATE_FORMAT = "d-m-Y"
SHORT_DATETIME_FORMAT = "d-m-Y G.i"
FIRST_DAY_OF_WEEK = 1  # Monday

# Date input formats
DATE_INPUT_FORMATS: list[str] = [
    "%Y-%m-%d",  # ISO format: '2023-03-15'
    "%d-%m-%Y",  # '25-10-2009'
    "%d/%m/%Y",  # '25/10/2009'
    "%d-%m-%y",  # '25-10-09'
    "%d/%m/%y",  # '25/10/09'
    "%d %b %Y",  # '25 Oct 2006',
    "%d %B %Y",  # '25 October 2006'
    "%m/%d/%y",  # '10/25/06'
    "%m/%d/%Y",  # '10/25/2009'
]

# Time input formats
TIME_INPUT_FORMATS: list[str] = [
    "%H:%M:%S",  # '14:30:59'
    "%H.%M.%S",  # '14.30.59'
    "%H.%M",  # '14.30'
    "%H:%M",  # '14:30'
    "%H:%M:%S.%f",  # '14:30:59.000200'
    "%H.%M.%S.%f",  # '14.30.59.000200'
]

# Datetime input formats
DATETIME_INPUT_FORMATS: list[str] = [
    f"{format_}" for format_ in DATE_INPUT_FORMATS for time_format in TIME_INPUT_FORMATS
]

# Decimal and thousand separators
DECIMAL_SEPARATOR: str = ","
THOUSAND_SEPARATOR: str = "."

# Number grouping separator
# It is used for thousands in this configuration
NUMBER_GROUPING: int = 3
