# The date and time format strings use the Django date format syntax,
# see https://docs.djangoproject.com/en/dev/ref/templates/builtins/#date
DATE_FORMAT = "Y-m-d"
TIME_FORMAT = "H:i"
DATETIME_FORMAT = "Y-m-d H:i"
YEAR_MONTH_FORMAT = "Y-m"
MONTH_DAY_FORMAT = "m-d"
SHORT_DATE_FORMAT = "Y/m/d"
SHORT_DATETIME_FORMAT = "Y/m/d H:i"

# The input format strings use the Python strftime format syntax,
# see https://docs.python.org/library/datetime.html#strftime-strptime-behavior
DATE_INPUT_FORMATS = (
    "%Y-%m-%d", "%m/%d/%Y", "%m/%d/%y",  # Django default
)
TIME_INPUT_FORMATS = (
    "%H:%M:%S", "%H:%M",  # Django default
)
DATETIME_INPUT_FORMATS = (
    "%Y-%m-%d %H:%M:%S", "%Y-%m-%d %H:%M",  # Django default
    "%Y/%m/%d %H:%M:%S", "%Y/%m/%d %H:%M",
)

# The decimal and thousand separator strings
DECIMAL_SEPARATOR = "."
THOUSAND_SEPARATOR = ","
NUMBER_GROUPING = 3
