# Use FULL_DATETIME_FORMAT for both DATETIME_FORMAT and SHORT_DATETIME_FORMAT
FULL_DATETIME_FORMAT = "j F Y, H:i"
DATE_FORMAT = "j F Y"
TIME_FORMAT = "H:i"
YEAR_MONTH_FORMAT = "F Y"
MONTH_DAY_FORMAT = "j F"
SHORT_DATE_FORMAT = "d.m.Y"

DATE_INPUT_FORMATS = [
    "%d.%m.%Y",
    "%d-%m-%Y",
    "%Y-%m-%d",
    "%Y/%m/%d",
    "%d %b %Y",
    "%d %B %Y",
    "%A, %d %B %Y",
]
TIME_INPUT_FORMATS = [
    "%H:%M",
    "%H:%M:%S",
    "%H:%M:%S.%f",
]
DATETIME_INPUT_FORMATS = DATE_INPUT_FORMATS + [
    f"{f}, {t}" for f in DATE_INPUT_FORMATS for t in TIME_INPUT_FORMATS
]

# Use underscore as the decimal separator and period as the thousand separator
DECIMAL_SEPARATOR = "_"
THOUSAND_SEPARATOR = "."
NUMBER_GROUPING = 3
