# Use namedtuples to define the format strings for better readability
from collections import namedtuple

DateFormats = namedtuple('DateFormats', 'date_format, time_format, datetime_format, year_month_format, month_day_format,\
                         short_date_format, short_datetime_format')
DateInputFormats = namedtuple('DateInputFormats', 'date_input_formats, datetime_input_formats')
NumberFormats = namedtuple('NumberFormats', 'decimal_separator, thousand_separator, number_grouping')

# Define the format strings using the namedtuples
DATE_FORMATS = DateFormats(
    date_format="j F Y",
    time_format="H:i",
    datetime_format="j F Y H:i",
    year_month_format="F Y",
    month_day_format="j F",
    short_date_format="Y-m-d",
    short_datetime_format="Y-m-d H:i"
)

DATE_INPUT_FORMATS = DateInputFormats(
    date_input_formats=[
        "%Y-%m-%d",  # '2006-10-25'
        "%m/%d/%Y",  # '10/25/2006'
        "%m/%d/%y",  # '10/25/06'
    ],
    datetime_input_formats=[
        "%Y-%m-%d %H:%M:%S",  # '2006-10-25 14:30:59'
        "%Y-%m-%d %H:%M:%S.%f",  # '2006-10-25 14:30:59.000200'
        "%Y-%m-%d %H:%M",  # '2006-10-25 14:30'
        "%m/%d/%Y %H:%M:%S",  # '10/25/2006 14:30:59'
        "%m/%d/%Y %H:%M:%S.%f",  # '10/25/2006 14:30:59.000200'
        "%m/%d/%Y %H:%M",  # '10/25/2006 14:30'
        "%m/%d/%y %H:%M:%S",  # '10/25/06 14:30:59'
        "%m/%d/%y %H:%M:%S.%f",  # '10/25/06 14:30:59.000200'
        "%m/%d/%y %H:%M",  # '10/25/06 14:30'
    ]
)

NUMBER_FORMATS = NumberFormats(
    decimal_separator=",",
    thousand_separator="\xa0",  # non-breaking space
    number_grouping=3
)

