(function ($) {
  if ($.fn.select2 && $.fn.select2.amd) {
    var e = $.fn.select2.amd;

    e.define("select2/i18n/af", [], function () {
      return {
        errorLoading: function () {
          return "Die resultate kon nie gelaai word nie.";
        },
        inputTooLong: function (e) {
          var n = e.input.length - e.maximum;
          var r = "Verwyders asseblief " + n + " character";
          return 1 != n ? (r += "s" ) : r;
        },
        inputTooShort: function (e) {
          return "Voer asseblief " + (e.minimum - e.input.length) + " of meer karakters";
        },
        loadingMore: function () {
          return "Meer resultate word gelaai…";
        },
        maximumSelected: function (e) {
          var n = "Kies asseblief net " + e.maximum + " item";
          return 1 != e.maximum ? (n += "s" ) : n;
        },
        noResults: function () {
          return "Geen resultate gevind";
        },
        searching: function () {
          return "Besig…";
        },
        removeAllItems: function () {
          return "Verwyder alle items";
        }
      };
    });

    e.define("select2/i18n/af", [], function () {
      return {
        errorLoading: function () {
          return "The results could not be loaded.";
        },
        inputTooLong: function (e) {
          var n = e.input.length - e.maximum;
          var r = "Please delete " + n + " character";
          return 1 != n ? (r += "s" ) : r;
        },
        inputTooShort: function (e) {
          return "Please enter " + (e.minimum - e.input.length) + " or more characters";
        },
        loadingMore: function () {
          return "Loading more results…";
        },
        maximumSelected: function (e) {
          var n = "Please select only " + e.maximum + " item";
          return 1 != e.maximum ? (n += "s" ) : n;
        },
        noResults: function () {
          return "No results found";
        },
        searching: function () {
          return "Searching…";
        },
        removeAllItems: function () {
          return "Remove all items";
        }
      };
    });

    e.define;
    e.require;
  }
}(jQuery));
