// typescript
import select2 from 'select2';

const GermanTranslation = {
  errorLoading: function () {
    return "Die Ergebnisse konnten nicht geladen werden.";
  },
  inputTooLong: function (e) {
    return `Bitte ${e.input.length - e.maximum} Zeichen weniger eingeben`;
  },
  inputTooShort: function (e) {
    return `Bitte ${e.minimum - e.input.length} Zeichen mehr eingeben`;
  },
  loadingMore: function () {
    return "Lade mehr Ergebnisse…";
  },
  maximumSelected: function (e) {
    let n = `Sie können nur ${e.maximum} Element`;
    if (e.maximum !== 1) {
      n += "e";
    }
    return n + " auswählen";
  },
  noResults: function () {
    return "Keine Übereinstimmungen gefunden";
  },
  searching: function () {
    return "Suche…";
  },
  removeAllItems: function () {
    return "Entferne alle Elemente";
  },
};

if (select2 && select2.amd) {
  select2.amd.define("select2/i18n/de", [], function () {
    return GermanTranslation;
  });
}
