(function () {
  if (jQuery && jQuery.fn && jQuery.fn.select2 && jQuery.fn.select2.amd) {
    var n = jQuery.fn.select2.amd;

    n.define("select2/i18n/mk", [], function () {
      return {
        inputTooLong: function (params) {
          var message =
            "Ве молиме внесете " +
            params.maximum +
            (params.maximum !== 1 ? "и" : " помалку карактер");
          return message;
        },

        inputTooShort: function (params) {
          var message =
            "Ве молиме внесете уште " +
            params.maximum +
            (params.maximum !== 1 ? "и" : " карактер");
          return message;
        },

        loadingMore: function () {
          return "Вчитување резултати…";
        },

        maximumSelected: function (params) {
          var message =
            "Можете да изберете само " +
            params.maximum +
            (params.maximum !== 1 ? " ставки" : " ставка");
          return message;
        },

        noResults: function () {
          return "Нема пронајдено совпаѓања";
        },

        searching: function () {
          return "Пребарување…";
        },

        removeAllItems: function () {
          return "Отстрани ги сите предмети";
        },
      };
    });

    n.define && n.require();
  }
})();
