/*! Select2 4.0.13 | https://github.com/select2/select2/blob/master/LICENSE.md */

(function ($, _, S2) {
    'use strict';

    // Slovak translation
    S2.define('select2/i18n/sk', [], function () {
        var errorLoading = 'Výsledky sa nepodarilo načítať.';
        var inputTooLong = function (args) {
            var t = args.input.length - args.maximum;
            if (t === 1) {
                return 'Prosím, zadajte o jeden znak menej.';
            } else if (t >= 2 && t <= 4) {
                return 'Prosím, zadajte o ' + _.pluralize(t, ['dva', 'tri', 'štyri']) + ' znaky menej.';
            } else {
                return 'Prosím, zadajte o ' + t + ' znakov menej.';
            }
        };
        var inputTooShort = function (args) {
            var t = args.minimum - args.input.length;
            if (t === 1) {
                return 'Prosím, zadajte ešte jeden znak.';
            } else if (t <= 4) {
                return 'Prosím, zadajte ešte ' + _.pluralize(t, ['ďalší', 'ďalšie', 'ďalšie']) + '.';
            } else {
                return 'Prosím, zadajte ešte ' + t + ' znakov.';
            }
        };
        var loadingMore = 'Načítanie ďalších výsledkov…';
        var maximumSelected = function (args) {
            var s = args.maximum - args.selected.length;
            if (s === 1) {
                return 'Môžete zvoliť iba jednu položku.';
            } else if (s >= 2 && s <= 4) {
                return 'Môžete zvoliť najviac ' + _.pluralize(args.maximum, ['dve', 'tri', 'štyri']) + '.';
            } else {
                return 'Môžete zvoliť najviac ' + args.maximum + ' položiek.';
            }
        };
        var noResults = 'Nenašli sa žiadne položky.';
        var searching = 'Vyhľadávanie…';
        var removeAllItems = 'Odstrániť všetky položky.';

        return {
            errorLoading: errorLoading,
            inputTooLong: inputTooLong,
            inputTooShort: inputTooShort,
            loadingMore: loadingMore,
            maximumSelected: maximumSelected,
            noResults: noResults,
            searching: searching,
            removeAllItems: removeAllItems
        };
    });
}(jQuery, _, Select2));
